using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;using EnumDic.System;public class StageTextC : MonoBehaviour{    [SerializeField]    private Text _text;    private string _stageName;    private char _nameBit;    // Start is called before the first frame update    void Start()    {        switch (GameData.GameMode)        {            case MODE_GAMEMODE.Normal:                _stageName = StageData.NamesNormalStage[GameData.Language, (GameData.Round + 4) / 5];                break;            case MODE_GAMEMODE.MultiTower:                _stageName = StageData.NamesMultiTowerStage[GameData.Language, (GameData.Round + 4) / 5];                break;        }        StartCoroutine(Anim());    }    private IEnumerator Anim()    {        int count = _stageName.Length;        for(int i = 1; i <= count; i++)        {            string text = "";            for(int j = 0; j < i; j++)            {                text += _stageName[j];                            }            _text.text = text;            yield return new WaitForSeconds(0.12f);        }        yield return new WaitForSeconds(1.0f);        Destroy(gameObject);    }}