using UnityEngine;public class TrapC : MonoBehaviour{    Vector3 velocity, _posOwn;    int i;    public ExpC ExpPrefab;    /// <summary>    /// スピーカ    /// </summary>    private AudioSource _audioGO;    public AudioClip expS;    private void Start()    {        _audioGO = GameObject.Find("AudioManager").GetComponent<AudioSource>();    }    private void FixedUpdate()    {        if (GetComponent<EMCoreC>().DeleteMissileCheck())        {            Explosion();        }    }    public void Explosion()    {        _posOwn = transform.position;        for (i = 0; i < 5; i++)        {            float angle2 = Random.Range(0, 360);            Quaternion rot2 = transform.localRotation;            ExpC shot2 = Instantiate(ExpPrefab, _posOwn, rot2);            shot2.ShotEXP(angle2, Random.Range(1, 10.0f), 0.5f);        }        _audioGO.PlayOneShot(expS);    }}